});    });    /* search  ajax start*/    $(document).ready(function () {        console.log('search part here');        $("input").keyup(function () {            search();        });        $("select")                .change(function () {                    search();                });        //$("#search-form").html("");        $("#brand").html("<option value=''>Merk / Model</option>");        // $("#prijs-van").html("");        // $("#prijs-tot").html("");        // $("#year1").html("");        // $("#year2").html("");        // $("#kmstand-van").html("");        // $("#kmstand-tot").html("");        $("#carrosserie").html("<option value='' >Carrosserie</option>");        //$("#aantal-deuren").html("");        $("#transmissie").html("<option value='' >Transmissie</option>");        $("#brandstof").html("<option value='' >Brandstof</option>");        /*  search form data start*/        $.ajax({            url: "<?php echo base_url(); ?>mobile/ajax_search_form",            data: {aanbod:<?php echo $aanbod; ?>},            type: "POST",            datatype: 'json',            success: function (result) {                //  console.log(result);                var dataArray = [];                var brands = [];                var brandstofs = [];                var versnellings = [];                var carrosseries = [];                var obj = jQuery.parseJSON(result);                for (key in obj)                    dataArray.push([key.toString(), obj [key]]);                for (i = 0; i < dataArray.length; i++) {                    /* for bands */                    var brand = dataArray[i][1].brand;                    var found = jQuery.inArray(brand, brands);                    if (found >= 0) {                    } else {                        // Element was not found, add it.                        brands.push(brand);                        $("#brand").append("<option value=" + brand + " >" + brand + "</option>");                    }                    /* end for bands */                    /* for brandstof */                    var brandstof = dataArray[i][1].brandstof;                    var found = jQuery.inArray(brandstof, brandstofs);                    if (found >= 0) {                    } else {                        // Element was not found, add it.                        brandstofs.push(brandstof);                        $("#brandstof").append("<option value=" + brandstof + "  >" + brandstof + "</option>");                    }                    /* end for brandstof */                    /* for versnelling */                    var versnelling = dataArray[i][1].versnelling;                    var found = jQuery.inArray(versnelling, versnellings);                    if (found >= 0) {                    } else {                        // Element was not found, add it.                        versnellings.push(versnelling);                        $("#transmissie").append("<option value=" + versnelling + "  >" + versnelling + "</option>");                    }                    /* end for versnelling */                    /* for Carrosserie */                    var carrosserie = dataArray[i][1].type;                    var found = jQuery.inArray(carrosserie, carrosseries);                    if (found >= 0) {                    } else {                        // Element was not found, add it.                        carrosseries.push(carrosserie);                        $("#carrosserie").append("<option value=" + carrosserie + "  >" + carrosserie + "</option>");                    }                    /* end for Carrosserie */                }                // console.log(dataArray[0]);                 //  console.log(brands);            }});        /*  search form data end*/    });    function search() {        var searchtext = $("#search-form").val();        var brand = $("#brand").val();        var prijsvan = $("#prijs-van").val();        var prijstot = $("#prijs-tot").val();        var bouwjaarVan = $("#year1").val();        var bouwjaarTot = $("#year2").val();        var kmstandVan = $("#kmstand-van").val();        var kmstandTot = $("#kmstand-tot").val();        var carrosserie = $("#carrosserie").val();        var zitplaatsen = $("#zitplaatsen").val();        var aantalDeuren = $("#aantal-deuren").val();        var transmissie = $("#transmissie").val();        var brandstof = $("#brandstof").val();        //                console.log(searchtext);         //                console.log(brand);         //                console.log(prijsvan);         //                console.log(prijstot);         //                console.log(bouwjaarVan);         //                console.log(bouwjaarTot);         //                console.log(kmstandVan);         //                console.log(kmstandTot);         //                console.log(carrosserie);         //                console.log(zitplaatsen);         //                console.log(aantalDeuren);         //                console.log(transmissie);         //                console.log(brandstof);        /*  search form data start*/        $.ajax({            url: "<?php echo base_url(); ?>mobile/ajax_search_object",            data: {                searchtext: searchtext,                brand: brand,                prijsvan: prijsvan,                prijstot: prijstot,                bouwjaarVan: bouwjaarVan,                bouwjaarTot: bouwjaarTot,                kmstandVan: kmstandVan,                kmstandTot: kmstandTot,                type: carrosserie,                zitplaatsen: zitplaatsen,                deuren: aantalDeuren,                versnelling: transmissie,                brandstof: brandstof            },            type: "POST",            datatype: 'json',            success: function (result) {                //console.log(result);                var format = '';                var full = '';                var formatgrid = '';                var fullgrid = '';                var dataArray = [];                var obj = jQuery.parseJSON(result);                for (key in obj)                    dataArray.push([key.toString(), obj [key]]);                for (i = 0; i < dataArray.length; i++) {                    var brand = dataArray[i][1].brand;                    var model = dataArray[i][1].model;                    var type = dataArray[i][1].type;                    var remoteFile = dataArray[i][1].remoteFile;                    var showroomprijs = dataArray[i][1].showroomprijs;                    var buildYear = dataArray[i][1].buildYear;                    var voertuig_id = dataArray[i][1].voertuig_id;                    var kilometerstand = dataArray[i][1].kilometerstand;                    /* for grid view */                    format = '';                    format = format + '<div class="div-box">';                    format = format + '<div class="div-box-img">';                    format = format + '<img src="' + remoteFile + '" alt="#" class="center car-img">';                    format = format + '</div>';                    format = format + '<div class="div-box-details">';                    format = format + '<h4>' + brand + ' ' + model + ' ' + type + '</h4>';                    format = format + '<p></p>';                    format = format + '</div>';                    format = format + '<div class="div-box-price">';                    format = format + '<p>Prijs: €  ' + showroomprijs + ' <br>Bouwjaar: ' + buildYear + ' <br>Kmstand: ' + kilometerstand + ' km</p>';                    format = format + '</div>';                    format = format + '<div class="div-box-arrow">';                    format = format + '<a data-toggle="modal" role="button" href="#TaskListDialog" onclick="object(' + voertuig_id + ')"><img src="<?php echo base_url(); ?>/assets/images/arrow.png" alt=""> </a>';                    format = format + '</div>';                    format = format + '</div>';                    full = full + format;                }                // $("#list-view").html(full);                // $("#grid-view").html(fullgrid);                $("#list-view").html(full);                console.log(full);            }});        /*  search form data end*/    }    function reset() {        $("input").val("");        //  $("select").val("0");        $('select option[value=""]').attr('selected', 'selected');    }    /* search  ajax start*/
